import Link from 'next/link';
import React, { useEffect, useState } from 'react';

const Home = ({ namename }) => {
    const [item, setItem] = useState('');
    const [name, setName] = useState(namename);


    useEffect(() => {
        setInterval(() => {
            setName('김경남');
        }, 1000);
        // setName('componentDidMount');
        // setTimeout(() => {
        //   setName('도도');
        // }, 1000)
    }, []);

    return (
        <>
            <div>
                <h1>안녕하세요 {name}입니다!</h1>
                <Link href={{ pathname: 'user/profile', query: { name: '오형남' } }} as={'user/profile'}><a>프로필</a></Link>
            </div>
        </>
    );
}

Home.getInitialProps = async (context) => {
    const url = fetch('https://jsonplaceholder.typicode.com/photos?id=200').then(function(response) {
        return response.json();
    });
    console.log(url);
    return { namename: '오형남' };
}

export default Home;
