import Link from 'next/link';
import React, { useEffect, useState } from 'react';

const Home = ({ imageUrl }) => {
    const [item, setItem] = useState('');
    const [name, setName] = useState(imageUrl);


    useEffect(() => {
        setName('김경남');
    }, []);

    return (
        <>
            <div>
                <h1>안녕하세요 {name}입니다!</h1>
                <Link href={{ pathname: 'user/profile', query: { name: '오형남' } }} as={'user/profile'}><a>프로필</a></Link>
            </div>
        </>
    );
}

Home.getInitialProps = async (context) => {
    const data = await fetch('https://jsonplaceholder.typicode.com/photos?id=200').then((response) => {
        return response.json();
    });
    console.log('imageUrl', data[0].url);
    return { imageUrl: data[0].url };
}

export default Home;
